import { useContext, useEffect, useState } from 'react'
import './Footer.scss'

const Footer = ({ pageNumber, maxPage, jumpToPage }) => {
  const [maximum, setMaximum] = useState(maxPage)

  useEffect(() => {  
    if (maximum.includes('&q')) {
      let newPg = maxPage.split('&')[0]
      setMaximum(parseInt(newPg))
    } else {
      setMaximum()
    }
  }, [maximum])


  return (
    <footer className='footer' data-testid='footer'>
      { parseInt(pageNumber) > 1 && 
        <button 
          onClick={() => jumpToPage(parseInt(pageNumber) - 1)}>
          ←
        </button>
      }
      <p>{ pageNumber }</p>
      { parseInt(pageNumber) < maxPage && 
        <button 
          onClick={() => jumpToPage(parseInt(pageNumber) + 1)}>
          →
        </button>
      }
    </footer>
  )
}

export default Footer
